import React, { useState, useEffect } from "react";
import "../../index.css";
import axios from 'axios';

const Home = () => {
  const [users, setUsers] = useState([]);

  const config = {
    headers: { 'Authorization': 'Bearer o7Ytbt9XQLI3PgtebJfKSXKEf0XHU74Y' }
  };
  
  axios.get('https://spe-academy.spesolution.net/api/recruitment', config)
    .then(response => {
      //console.log(response.data);
      setUsers(response.data);
    })
    .catch(error => {
      console.error(error);
    });

  console.log(users);

  return (
          <div class="bg-light">
            <div class="container px-5 my-5">
              <div class="text-center mb-5">
                <h1 class="fw-bolder">List User</h1>
              </div>

              <div class="row gx-5 content-center">
                {users.map((user) => {
                    return (
                      <div class="col-xl-4 mb-5">
                        {user.quantity}
                        {user.product.name}
                      </div>
                    );
                  })}
              </div>
            </div>

          </div>
  );
}

export default Home;
