import React, { useState} from "react";
import "../../index.css";
import axios from 'axios';

const Home = () => {
  const [users, setUsers] = useState([]);

  const config = {
    headers: { 'Authorization': 'Bearer o7Ytbt9XQLI3PgtebJfKSXKEf0XHU74Y' }
  };
  
  axios.get('https://spe-academy.spesolution.net/api/recruitment', config)
    .then(response => {
      //console.log(response.data);
      setUsers(response.data);
    })
    .catch(error => {
      console.error(error);
    });

  console.log(users);

  return (
  <header className="py-4 px-4">
    <table class="table">
      <thead>
        <tr className="table-dark">
          <th scope="col">PRODUCT</th>
          <th scope="col">QUANTITY</th>
          <th scope="col">SUBTOTAL</th>
        </tr>
      </thead>
      <tbody>
        {users.map((user) => {
          return (
            <tr>
              <td>{user.product.name}</td>
              <td>{user.quantity}</td>
              <td>{user.product.price}</td>
            </tr>
          );
        })}
      </tbody>
    </table>
  </header>




  );
}

export default Home;
