import React, { useState, useEffect} from "react";
import "../../index.css";
import axios from 'axios';

const Home = () => {
  const [datas, setData] = useState([]);

  useEffect(() => {
    const getApi = async () => {
      try {
        await axios
          .get('https://spe-academy.spesolution.net/api/recruitment', {
            headers: {
              Authorization: 'Bearer o7Ytbt9XQLI3PgtebJfKSXKEf0XHU74Y',
            },
          })
          .then((res) => {
            setData(res.data);
          });
      } catch (error) {
        console.log(error);
      }
    };
    getApi();
  }, []);

  const total = datas.reduce((acc, row) => acc + (row.product.price * row.quantity), 0);

  return (
  <header className="py-4 px-4 bg-light">
     <div className="d-flex justify-content-center">
        <h1 className="mb-5 mt-5">SPE Frontend Shop</h1>
      </div>
    <table className="table">
      <thead>
        <tr className="table-dark text-center">
          <th scope="col">PRODUCT</th>
          <th scope="col">QUANTITY</th>
          <th scope="col">SUBTOTAL</th>
        </tr>
      </thead>
      <tbody>
        {datas.map((data) => {
          return (
            <tr>
              <td>
                <div className="row m-1">
                  <div className="col-4">
                    <img 
                      className="img-thumbnail"
                      scr={data.product.media_url}
                      alt=""/>
                  </div>
                          
                  <div className="col-6">
                    <div className="fs-5 text-primary">{data.product.code}</div>
                    <div className="fs-5">{data.product.name}</div>
                    <div className="fs-6 fw-lighter">IDR. {data.product.price.toLocaleString()}</div>
                    <div className="fs-9 text-danger">{data.product.stock} in stock</div>
                  </div>
                </div>
              </td>

              <td>
                <input className="kotak" type="text" placeholder={data.quantity} />
                </td>
              <td>{(data.product.price * data.quantity).toLocaleString()}</td>
            </tr>
          );
        })}
      </tbody>
      <thead>
        <tr className="table-dark">
          <th scope="col"></th>
          <th scope="col">TOTAL</th>
          <th scope="col">{total.toLocaleString()}</th>
        </tr>
      </thead>
    </table>
  </header>




  );
}

export default Home;
