import React, { useState} from "react";
import "../../index.css";
import axios from 'axios';

const Home = () => {
  const [users, setUsers] = useState([]);

  const config = {
    headers: { 'Authorization': 'Bearer o7Ytbt9XQLI3PgtebJfKSXKEf0XHU74Y' }
  };
  
  axios.get('https://spe-academy.spesolution.net/api/recruitment', config)
    .then(response => {
      //console.log(response.data);
      setUsers(response.data);
    })
    .catch(error => {
      console.error(error);
    });

  console.log(users);

  return (
  <div>
    <header className="py-4">
      <div className="py-3 container mt-4">
        
        <div className="row justify-content-center">
          {users.map((user) => {
            return (

                        <div class="card mb-4" style={{width : '41rem'}}>
                        <div className="row m-1">
                          <div className="col-2">
                            <img className="card-img-top"  scr={users.productmedia_url}alt=""/>
                          </div>
                          
                          <div className="col-10">
                            <h3>{user.product.name}</h3>
                          </div>
                          
                        </div>
                      </div>
            );
          })}
  
        </div>
  
      </div>
    </header>
  </div>


  );
}

export default Home;
