import React, { useState} from "react";
import "../../index.css";
import axios from 'axios';

const Home = () => {
  const [users, setUsers] = useState([]);

  const config = {
    headers: { 'Authorization': 'Bearer o7Ytbt9XQLI3PgtebJfKSXKEf0XHU74Y' }
  };
  
  axios.get('https://spe-academy.spesolution.net/api/recruitment', config)
    .then(response => {
      //console.log(response.data);
      setUsers(response.data);
    })
    .catch(error => {
      console.error(error);
    });

  console.log(users);

  return (
  <header className="py-4 px-4">
    <table class="table">
      <thead>
        <tr className="table-dark">
          <th scope="col">PRODUCT</th>
          <th scope="col">QUANTITY</th>
          <th scope="col">SUBTOTAL</th>
        </tr>
      </thead>
      <tbody>
        {users.map((user) => {
          return (
            <tr>
              <td>
                <div className="row m-1">
                  <div className="col-2">
                    <img className="card-img-top"  scr={user.product.media_url} alt=""/>
                  </div>
                          
                  <div className="col-10">
                    <p>{user.product.name}</p>
                    <p>IDR. {user.product.price}</p>
                    <p>{user.product.stock} in stock</p>
                  </div>
                          
                </div>
              </td>
              <td><input type="text" placeholder={user.quantity} /></td>
              <td>{user.product.price * user.quantity}</td>
            </tr>
          );
        })}
      </tbody>
      <thead>
        <tr className="table-dark">
          <th scope="col"></th>
          <th scope="col">TOTAL</th>
          <th scope="col">L</th>
        </tr>
      </thead>
    </table>
  </header>




  );
}

export default Home;
