     <div class="d-flex justify-content-center">
        <h1 class="mb-5 mt-5">SPE Frontend Shop</h1>
      </div>
    <table class="table">
      <thead>
        <tr className="table-dark text-center">
          <th scope="col">PRODUCT</th>
          <th scope="col">QUANTITY</th>
          <th scope="col">SUBTOTAL</th>
        </tr>
      </thead>
      <tbody>
        {users.map((user) => {
          return (
            <tr>
              <td>
                <div className="row m-1">
                  <div className="col-4">
                    <img scr={user.product.media_url} alt=""/>
                  </div>
                          
                  <div className="col-6">
                    <div class="fs-5 text-primary">{user.product.code}</div>
                    <div class="fs-5">{user.product.name}</div>
                    <div class="fs-6 fw-lighter">IDR. {user.product.price}</div>
                    <div class="fs-9 text-danger">{user.product.stock} in stock</div>
                  </div>
                </div>
              </td>

              <td>
                <input className="kotak" type="text" placeholder={user.quantity} />
                </td>
              <td>{user.product.price * user.quantity}</td>
            </tr>
          );
        })}
      </tbody>
      <thead>
        <tr className="table-dark">
          <th scope="col"></th>
          <th scope="col">TOTAL</th>
          <th scope="col"></th>
        </tr>
      </thead>
    </table>
  </header>




  );
}

export default Home;
